  async function fetchApi(url, v=0) {
    if (v === 1) {
      let result = new XMLHttpRequest();
      result.onreadystatechange = function() {
        return this.response;
      };
      result.open('GET', url);
      result.send();
    }
    let result = await fetch(url);
    let json = await result.json();
    return json;
  };
  async function nextPage(power) {
    let nbPage = power.state.results.nbPages || 0;
    let currentPage = power.state.results.page + 1;
    if (currentPage < nbPage) {
      let result = await fetchApi(power.state.api+'&page='+currentPage);
      power.setState({results: result});
    }
  };
  async function openLink(url) {
    // document.querySelector('[class="i-frame"]').innerHTML = `<iframe src=${url}/>`;
    let html = await fetchApi(url, 1);
    document.querySelector('[class="i-frame"]').innerHTML = html;
  }
  // async function componentWillMount() {
