import React, { Component } from 'react';
import { fetchApi, nextPage }  from './common';

class Anime extends Component {
  constructor() {
    super();
    this.state = {
      api: 'http://hn.algolia.com/api/v1/search_by_date?query=comics&tags=story',
      results: [],
      topTen: []
    }
    // this.nextPage= CommonMethods.nextPage(this);
  }
  // async fetchApi(url) {
  //   let result = await fetch(url);
  //   let json = await result.json();
  //   return json;
  // }
  // async nextPage() {
  //   let nbPage = this.state.results.nbPages || 0;
  //   let currentPage = this.state.results.page + 1;
  //   if (currentPage < nbPage) {
  //     let result = await this.fetchApi(this.state.api+'&page='+currentPage);
  //     this.setState({results: result});
  //   }
  // }
  async componentWillMount() {
    let result = await fetchApi(this.state.api);
    this.setState({results: result});
  }

  render() {
    let results = this.state.results;
    let hits = results.hits;
    let fun = nextPage();
    if (hits) {
      return (
        <div className="App">
          <h2>{hits.map((item) => (<span className="list" key={item.url}><a href={item.url} target="_blank" rel="noopener noreferrer">{item.title}</a><br/>-By {item.author}</span>))}</h2>
          <button onClick={ fun(this) }>Next>></button>
        </div>
      );
    } else {
      return (<div className="App">Loading Please Wait</div>);
    }
  }
}
export default Anime;
